CREATE TABLE PROPERTY (PROPERTY_ID BIGINT AUTO_INCREMENT NOT NULL, PROPERTY_KEY VARCHAR(255) NOT NULL, NAME VARCHAR(255) NOT NULL, VALUE VARCHAR(255) NOT NULL, PRIMARY KEY (PROPERTY_ID))
CREATE TABLE CURRENCY (CURRENCY_ID BIGINT AUTO_INCREMENT NOT NULL, CODE VARCHAR(255), COUNTRY VARCHAR(255), NAME VARCHAR(255), PRIMARY KEY (CURRENCY_ID))
CREATE TABLE TRANSACTION (TRANSACTION_ID BIGINT AUTO_INCREMENT NOT NULL, DATE DATE, DEFERRAL_DATE DATE, EXCHANGE_RATE DECIMAL(19,6), EXPIRY_DATE DATE, FEE_IN_TRANSACTION_CURRENCY DECIMAL(38), PRICE DECIMAL(38), QUANTITY DECIMAL(38), RESTRICTION_DATE DATE, TRANSACTION_TYPE VARCHAR(255), VESTING_DATE DATE, CURRENCY_ID BIGINT, PRODUCT_ID BIGINT, PRIMARY KEY (TRANSACTION_ID))
CREATE TABLE PRODUCT (PRODUCT_ID BIGINT AUTO_INCREMENT NOT NULL, PRODUCT_TYPE VARCHAR(31), NAME VARCHAR(255), BBG_SYMBOL VARCHAR(255), LAST_MODIFIED_DATE DATETIME, MARKET_PRICE DECIMAL(19,4), MARKET_PRICE_DATE DATETIME, UPDATE_STATUS VARCHAR(255), CURRENCY_ID BIGINT, STRIKE_PRICE DECIMAL(19,2), UNDERLYING_ID BIGINT, PRIMARY KEY (PRODUCT_ID))
CREATE TABLE EXCHANGE_RATE (EXCHANGE_RATE_ID BIGINT AUTO_INCREMENT NOT NULL, BBG_SYMBOL VARCHAR(255), LAST_MODIFIED_DATE DATETIME, EXCHANGE_RATE DECIMAL(19,4), EXCHANGE_RATE_DATE DATETIME, UPDATE_STATUS VARCHAR(255), FROM_CURRENCY_ID BIGINT, TO_CURRENCY_ID BIGINT, PRIMARY KEY (EXCHANGE_RATE_ID))
ALTER TABLE TRANSACTION ADD CONSTRAINT FK_TRANSACTION_PRODUCT_ID FOREIGN KEY (PRODUCT_ID) REFERENCES PRODUCT (PRODUCT_ID)
ALTER TABLE TRANSACTION ADD CONSTRAINT FK_TRANSACTION_CURRENCY_ID FOREIGN KEY (CURRENCY_ID) REFERENCES CURRENCY (CURRENCY_ID)
ALTER TABLE PRODUCT ADD CONSTRAINT FK_PRODUCT_UNDERLYING_ID FOREIGN KEY (UNDERLYING_ID) REFERENCES PRODUCT (PRODUCT_ID)
ALTER TABLE PRODUCT ADD CONSTRAINT FK_PRODUCT_CURRENCY_ID FOREIGN KEY (CURRENCY_ID) REFERENCES CURRENCY (CURRENCY_ID)
ALTER TABLE EXCHANGE_RATE ADD CONSTRAINT FK_EXCHANGE_RATE_FROM_CURRENCY_ID FOREIGN KEY (FROM_CURRENCY_ID) REFERENCES CURRENCY (CURRENCY_ID)
ALTER TABLE EXCHANGE_RATE ADD CONSTRAINT FK_EXCHANGE_RATE_TO_CURRENCY_ID FOREIGN KEY (TO_CURRENCY_ID) REFERENCES CURRENCY (CURRENCY_ID)
